<?php/** * The Template for displaying all single posts. * * @package metro-creativex */get_header(); ?>	<?php  while ( have_posts() ) : the_post(); ?>				<h1 class="insidepost" style="background-image:url(				<?php 				$metro_creativex_template_url = get_template_directory_uri();				if (has_post_format( 'aside' )) {					echo $metro_creativex_template_url.'/images/pt_aside.png';				} elseif (has_post_format( 'audio' )) {					echo $metro_creativex_template_url.'/images/pt_audio.png';				} elseif (has_post_format( 'chat' )) {					echo $metro_creativex_template_url.'/images/pt_chat.png';				} elseif (has_post_format( 'gallery' )) {					echo $metro_creativex_template_url.'/images/pt_gallery.png';				} elseif (has_post_format( 'image' )) {					echo $metro_creativex_template_url.'/images/pt_image.png';				} elseif (has_post_format( 'link' )) {					echo $metro_creativex_template_url.'/images/pt_link.png';				} elseif (has_post_format( 'quote' )) {					echo $metro_creativex_template_url.'/images/pt_quote.png';				} elseif (has_post_format( 'status' )) {					echo $metro_creativex_template_url.'/images/pt_status.png';				} elseif (has_post_format( 'video' )) {					echo $metro_creativex_template_url.'/images/pt_video.png';				} else {					echo $metro_creativex_template_url.'/images/pt_standard.png';				}				?>);"><?php the_title(); ?></h1>			<div class="insidepost_date"><?php echo get_the_date(); ?> - <?php the_category(', ') ?></div>		</div><!--/topside-->		<div id="content">			<div <?php post_class(); ?>>				<?php 					$metro_creativex_posttitle = get_the_title();					$metro_creativex_feat_image = wp_get_attachment_image_src( get_post_thumbnail_id( $post->ID ), 'single-post-thumbnail' );					if(isset($metro_creativex_feat_image[0])):						echo '<div class="post_img"><img src='.$metro_creativex_feat_image[0].' alt="'.$metro_creativex_posttitle.'"></div>';					endif;										the_content(); 					wp_link_pages();					edit_post_link( __('Edit post', 'metro-creativex'));				?>				<div class="clearfix"></div>				<?php 					if(has_tag()):						echo '<div class="tags">';						the_tags();							echo '</div>';					endif;					?> 			</div><!--/post-->			<?php metro_creativex_pagination(); ?>			<?php comments_template(); ?>		</div><!-- /content -->	<?php endwhile; ?><?php get_footer(); ?>